<?php error_reporting(E_ALL); ini_set('display_errors', 1); require 'db.php'; $username = 'admin.admin'; $password_plain = 'admin123'; $role = 'admin'; $hashed_password = password_hash($password_plain, PASSWORD_DEFAULT); $stmt = $conn->prepare("INSERT INTO users (username, password, role, created_at) VALUES (?, ?, ?, NOW())"); if (!$stmt) { die("Hazırlama hatası: " . $conn->error); } $stmt->bind_param("sss", $username, $hashed_password, $role); if ($stmt->execute()) { echo "✅ Admin başarıyla oluşturuldu."; } else { echo "❌ Hata oluştu: " . $stmt->error; } ?>